CREATE TABLE projects(id INTEGER NOT NULL PRIMARY KEY, code TEXT NOT NULL, directory TEXT, name TEXT);
CREATE TABLE tasks(id INTEGER NOT NULL PRIMARY KEY, project_id INTEGER NOT NULL, number INTEGER NOT NULL, active_subtask INTEGER, FOREIGN KEY(project_id) REFERENCES projects(id) ON DELETE CASCADE);
CREATE TABLE subtasks(id INTEGER NOT NULL PRIMARY KEY, task_id INTEGER NOT NULL, branch TEXT, description TEXT, active INTEGER NOT NULL DEFAULT 0, FOREIGN KEY(task_id) REFERENCES tasks(id) ON DELETE CASCADE);
CREATE TABLE stretches(id INTEGER NOT NULL PRIMARY KEY, subtask_id INTEGER NOT NULL, start INTEGER NOT NULL, end INTEGER, FOREIGN KEY(subtask_id) REFERENCES subtasks(id));